meta.script:
async function generateSitemap() {
  const res = await fetch('pages.json');
  const data = await res.json();
  const urls = data.pages;

  let sitemap = `\n`;
  sitemap += `\n`;

  urls.forEach(page => {
    sitemap += `  \n`;
    sitemap += `    ${page.url}\n`;
    sitemap += `    0.8\n`;
    sitemap += `  \n`;
  });

  sitemap += ``;

  document.body.textContent = sitemap;
}

// Run on load
window.onload = generateSitemap;
